@import "./variables";
// Typogrphy start

@mixin laptop-version() {
  @media screen and (min-width: $laptop-width) {
    @content;
  }
}
@mixin ipad-version() {
  @media screen and (min-width: $Ipad-width) {
    @content;
  }
}
@mixin small-mobile-version() {
  @media screen and (min-width: $min-mobile-width) and (max-width: $max-mobile-width) {
    @content;
  }
}
@mixin flex-to-inline() {
  display: flex;
  @content;
}

*,
*::after,
*::before {
  box-sizing: border-box;
}
html {
  font-size: 62.5%;
}
img {
