  h3 {
    font-size: 2.4rem;
  }
}

p {
  margin-top: 0;
}

a {
  text-decoration: none;
}

ul {
  margin: 0;
}

.link-arrow {
  color: #ff3400;
  text-transform: uppercase;
  font-weight: bold;
  font-size: 2rem;
}
.link-arrow::after {
  content: "-->";
  margin-left: 5px;
  transition: margin 0.25s;
}
.link-arrow:hover::after {
  margin-left: 10px;
}
@media screen and (min-width: 1024px) {
  .link-arrow {
    font-size: 1.5rem;
  }
}

.badges {
  border-radius: 20px;
  color: #fff;
  font-size: 1.6rem;
  font-weight: bold;
  padding: 1rem 2rem;
  white-space: nowrap;
}
@media screen and (min-width: 1024px) {
  .badges {
    font-size: 1.2rem;
  }
}

.badge--primary {
  margin-left: 5px;
}

.badge--secondary {
  background-color: #00d9ff;
  font-size: 2rem;
}
@media screen and (min-width: 768px) {
  .badge--secondary {
    font-size: 1.5rem;
  }
}

.list {
  list-style: none;
  padding-left: 0;
  color: #1b0760;
  display: flex;
